import React, { useEffect, useState } from "react";
import LeafletMap from "../components/LeafletMap";

const Explore = () => {
  const [advice, setAdvice] = useState("");
  const [quote, setQuote] = useState(null);
  const [typeFit, setTypeFit] = useState(null);

  useEffect(()=> {
    const fetchadvice = async () => {
    try {
      const response = await fetch("https://api.adviceslip.com/advice", {
        cache: "no-store"
      });
      const data = await response.json();
      setAdvice(data.slip.advice);
    } catch {
      setAdvice(null);
    }
  };
    const fetchQuote = async () => {
      try {
        const response = await fetch("https://api.quotable.io/random");
        const data = await response.json();
        setQuote(`"${data.content}" - ${data.author}`);
      } catch {
        setQuote(null);
      }
    };

    const fetchTypeFit = async () => {
      try {
        const res = await fetch("https://type.fit/api/quotes");
        const data = await res.json();
        const random = data[Math.floor(Math.random() * data.length)];
        setTypeFit(`"${random.text}" ‚Äî ${random.author || "Unknown"}`);
      } catch {
        setTypeFit(null);
      }
    };


    fetchadvice();
    fetchQuote();
    fetchTypeFit();
  }, []);

  return (
    <ol id="containers">
      <li>
        <div id="hero-container">
          <h2>Explore Locations</h2>
         
          <div style={{display: "flex", justifyContent: "center", alignItems: "center" }}>
            <LeafletMap />
          </div>
        </div>
      </li>
      <li id="intro-container">
        <div>
        </div>
        <div>
          <h2>Take it easy</h2>
          <span>
               <h4>Think Zen Thoughts:</h4>
               <p>{zenQuote}</p>
          </span>
        </div>
      </li>

    </ol>
    // <div>
    //   <h1>Explore Locations</h1>
    // <div style={{display: "flex", justifyContent: "center", alignItems: "center" }}>
    //   <LeafletMap />
    // </div>
    //  <div style={{ padding: "2rem", textAlign: "center" }}>
    //   <h1>Explore the World‚Ä¶ or Your Mind</h1>

    //   <div>
    //     <h3>Tip:</h3>
    //     <p style={{ fontStyle: "italic" }}>‚Äú{advice}‚Äù</p>
    //     {zenQuote && (
    //       <div style={{ marginTop: "1rem" }}>
    //         <h4>üßò Zen Thought</h4>
    //         <p style={{ fontStyle: "italic" }}>{zenQuote}</p>
    //       </div>
    //     )}
    //   </div>
    // </div>
    // </div>
  );
}

export default Explore;
