import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const SearchResults = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const query = queryParams.get("q") || "";
  const [results, setResults] = useState({ users: [], products: [] });

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/search?q=${encodeURIComponent(query)}`);
        setResults(res.data);
      } catch (error) {
        console.error("Search error:", error);
      }
    };

    if (query) {
      fetchResults();
    }
  }, [query]);

  return (
    <div>
      <h2>Search Results for: "{query}"</h2>
      <ol>
        <li>
          <h3>üõçÔ∏è Products</h3>
          {results.products.length > 0 ? (
            <ul className="search-products">
              {results.products.map((item) => (
                <li key={item.item_id}>
                  <img src={item.thumbnail_image} alt={item.item_title} width={80} />
                  <h3>{item.item_title}</h3>
                  <p>{item.brand}</p>
                  <p>${item.unit_price}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No matching products.</p>
          )}
          </li>
          <li>
            <h3>üë§ Users</h3>
            {results.users.length > 0 ? (
              <ul className="search-users">
                {results.users.map((user) => (
                  <li key={user.user_id}>
                    <img src={user.user_pfp_src} alt={user.user_name} width={50} />
                    <h4>{user.user_name}</h4>
                    <p>{user.user_about_me}</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>No matching users.</p>
            )}
          </li>
      </ol>
    </div>
  );
};

export default SearchResults;
