import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { BsVinyl, BsVinylFill } from "react-icons/bs";
import { FaBook } from "react-icons/fa6";
import Book from "../components/Book";
import Vinyl from "../components/Vinyl";

const UserDetails = () => {
  const { username } = useParams();
  const [profileUser, setProfileUser] = useState(null);
  const [products, setProducts] = useState([]);
  const [activeTab, setActiveTab] = useState("Vinyls");

  // Fetch products from public folder
  useEffect(() => {
    fetch(process.env.PUBLIC_URL + "/data/products.json")
      .then(res => res.json())
      .then(data => {
        setProducts(data.products);
      })
      .catch(err => console.error("[ERROR] Failed to fetch products:", err));
  }, []);

  // Always fetch full profile user data based on username
  useEffect(() => {
    if (username) {
      fetch(`/api/users/${username}`)
        .then(res => res.json())
        .then(data => {
          console.log("[DEBUG] Fetched profile user:", data);
          setProfileUser(data);
        })
        .catch(err => console.error("[ERROR] Failed to fetch user:", err));
    }
  }, [username]);

  // Helper to match product details
  const getProductById = (id) => {
    return products.find(product => product.item_id === id);
  };

  const renderGallery = () => {
    if (!profileUser || products.length === 0) return null;

    switch (activeTab) {
      case "Vinyls":
        return profileUser.user_music?.Vinyls?.map((vinyl, index) => {
          const product = getProductById(vinyl.item_id);
          if (!product) return null;
          return (
            <li key={`vinyl-${index}`}>
              <Vinyl
                title={product.item_title}
                authors={product.brand}
                coverUrl={product.thumbnail_image}
              />
            </li>
          );
        });

      case "CDs":
        return profileUser.user_music?.CDs?.map((cd, index) => {
          const product = getProductById(cd.item_id);
          if (!product) return null;
          return (
            <li key={`cd-${index}`}>
              <p>🎧 {product.item_title} - {product.brand}</p>
            </li>
          );
        });

      case "Books":
        return profileUser.user_books?.map((book, index) => {
          const product = getProductById(book.item_id);
          if (!product) return null;
          return (
            <li key={`book-${index}`}>
              <Book
                title={product.item_title}
                authors={product.brand}
                pages={["--"]}
                size="small"
                type="hard"
              />
            </li>
          );
        });

      default:
        return <li><p>This user has nothing to show!</p></li>;
    }
  };

  // Show loading until both sets of data are ready
  if (!profileUser || products.length === 0) return <p>loading . . .</p>;

  return (
    <ol id="containers">
      <li>
        <div id="hero-container" className="profile-container">
          <img
            id="banner-picture-img"
            className="profile-banner"
            src={profileUser.user_banner_src}
            alt="Banner"
          />

          <div id="profile-gallery">
            <ul id="profile-gallery-tabs">
              <li>
                <button onClick={() => setActiveTab("Vinyls")} tooltip="Vinyls">
                  <BsVinylFill />
                </button>
              </li>
              <li>
                <button onClick={() => setActiveTab("CDs")} tooltip="CDs">
                  <BsVinyl />
                </button>
              </li>
              <li>
                <button onClick={() => setActiveTab("Books")} tooltip="Books">
                  <FaBook />
                </button>
              </li>
            </ul>
            <ol>{renderGallery()}</ol>
          </div>

          <div id="profile-info">
            <img
              id="pfp"
              className="profile-pfp"
              src={profileUser.user_pfp_src}
              alt="Profile"
            />
            <h3>{profileUser.user_name}</h3>
            <ul id="profile-buttons">
              <li><button id="add-user-btn">Add User</button></li>
              <li><button id="dm-user-btn">Dm User</button></li>
              <li><button id="ignore-user-btn">Ignore User</button></li>
              <li><button id="block-user-btn">Block User</button></li>
            </ul>
          </div>
        </div>
      </li>
    </ol>
  );
};

export default UserDetails;
