import React, { useEffect, useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { HashLink } from "react-router-hash-link";
import { CurrentUserContext } from '../components/CurrentUserContext';
import axios from "axios";

const NavBar = () => {
  const { currentUser, logoutUser } = useContext(CurrentUserContext);
  const navigate = useNavigate(); 

  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchVisible, setSearchVisible] = useState(false);

  // Fetch users matching the query
  useEffect(() => {
    if (!searchQuery) {
      setSearchResults([]);
      return;
    }

    const delayDebounce = setTimeout(() => {
      axios.get('/api/users') // replace with optimized endpoint if needed
        .then(res => {
          const filtered = res.data.filter(user =>
            user.user_name.toLowerCase().includes(searchQuery.toLowerCase())
          );
          setSearchResults(filtered);
        })
        .catch(err => console.error("Search error:", err));
    }, 300); // debounce

    return () => clearTimeout(delayDebounce);
  }, [searchQuery]);

  const handleSignOut = () => {
    logoutUser();  
    navigate("/login");
  };

  return (
    <>
      <header id="nav-bar">
        <div id="nav-bar-left">
          <a href="/"><img id="logo" src="https://cdn-icons-png.flaticon.com/512/812/812680.png" /></a>
          <ol id="nav-bar-tabs">
            <li><Link to="/"><button>Home</button></Link></li>
            <li><Link to="/about"><button>About</button></Link></li>
            <li><Link to="/shop"><button>Shop</button></Link></li>
            <li><Link to="/explore"><button>Explore</button></Link></li>
          </ol>
        </div>

        <div id="nav-bar-right">
          <div id="nav-bar-misc">
            <span id="search-container" style={{ position: "relative" }}>
              <label
                id="search-label"
                htmlFor="search-input"
                onClick={() => setSearchVisible(!isSearchVisible)}
              >
                {isSearchVisible ? "‚úñÔ∏è" : "üîç"}
              </label>
              <input
                type="text"
                id="search-input"
                className={isSearchVisible ? "" : "invisible"}
                placeholder="Search users..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />

              {searchResults.length > 0 && (
                <ul
                  style={{
                    position: "absolute",
                    top: "2.5rem",
                    left: 0,
                    backgroundColor: "#fff",
                    border: "1px solid #ccc",
                    borderRadius: "4px",
                    zIndex: 1000,
                    listStyle: "none",
                    padding: "0.5rem",
                    width: "200px",
                  }}
                >
                  {searchResults.map((user) => (
                    <li key={user.user_id}>
                      <Link to={`/profile/${user.user_name}`} onClick={() => {
                        setSearchQuery("");
                        setSearchResults([]);
                        setSearchVisible(false);
                      }}>
                        {user.user_name}
                      </Link>
                    </li>
                  ))}
                </ul>
              )}
            </span>

            <span><Link to="/cart">üõí</Link></span>
          </div>

          <div id="nav-bar-user-drop">
            {currentUser ? (
              <span>
                <h3>How's it groovin' {currentUser.user_name}?</h3>
                <ul id="user-drop-options">
                  <li className="drop-option"><button id="drop-down">User dropdown ‚û§</button></li>
                  <li className="drop-option"><HashLink smooth to="/settings#user-settings">View Settings</HashLink></li>
                  <li className="drop-option"><HashLink smooth to="/settings#accessibility-settings">View Accessibility</HashLink></li>
                  <li className="drop-option"><HashLink smooth to="/settings#connection-settings">View More Settings</HashLink></li>
                  <li className="drop-option"><button onClick={handleSignOut}>Sign Out</button></li>
                </ul>
              </span>
            ) : (
              <span>
                <h3>Welcome, Guest!</h3>
                <ul id="user-drop-options">
                  <li className="drop-option"><button id="drop-down">User dropdown ‚û§</button></li>
                  <li className="drop-option"><Link to="/login">Login</Link></li>
                  <li className="drop-option"><Link to="/signup">Sign Up</Link></li>
                </ul>
              </span>
            )}

            {currentUser && (
              <Link to={`/profile/${currentUser.user_name}`}>
                <img id="pfp" src={currentUser.user_pfp_src} />
              </Link>
            )}
          </div>
        </div>
      </header>
    </>
  );
};

export default NavBar;
