import { BsVinyl,BsVinylFill  } from "react-icons/bs";
import { FaBook } from "react-icons/fa6";


import BannerPlaceholder from '../images/banner-placeholder.png';
import Book from '../components/Book';
import { useState } from "react";
const UserDetails = (currentUser) => {
  const [ activeTab, setActiveTab] = useState("Vinyls");
  const authors = ["username"];
    const pages = ["page1","page2", "page3"];

    const renderGallery = () => {
      if (activeTab == "Vinyls")  {
        currentUser.currentUser.user_books.map((book, index) => {
          <li key={index}><Book title="small book example" authors={authors} pages={pages} size="small" type="hard" /></li>
        })}
    }
    return (
        <ol id="containers">
          <li>
            <div id="hero-container" className="profile-container">
              <img id="banner-picture-img" className='profile-banner' src={currentUser.currentUser.user_banner_src} />

               <div id="profile-gallery">
                <ul id='profile-gallery-tabs'>
                  <li><button tooltip="Vinyls"><BsVinylFill /></button></li>
                  <li><button tooltip="CDs"><BsVinyl /></button></li>
                  <li><button tooltip="Books"><FaBook /></button></li>
                </ul>
                <ol>
                 
                </ol>
               </div>
               <div id="profile-info">
                  <img id="pfp" className='profile-pfp' src="https://i.pinimg.com/236x/17/57/1c/17571cdf635b8156272109eaa9cb5900.jpg" />
                  <h3>Username</h3>
                  <ul id="profile-buttons">
                    <li><button id="add-user-btn">Add User</button></li>
                    <li><button id="dm-user-btn">Dm User</button></li>
                    <li><button id="ignore-user-btn">Ignore User</button></li>
                    <li><button id="block-user-btn">Block User</button></li>
                  </ul>
               </div>                  
            </div>
          </li>
          </ol>
    );
  }
  
  export default UserDetails;
